* Adds a "Cc" address.* Note: this function works with the SMTP mailer on win32, not with the "mail" mailer.* @param string $address* @param string $name* @return boolean true on success, false if address already used*/&nbsp; public function AddCC($address, $name = '') {&nbsp;&nbsp;&nbsp; return $this-&gt;AddAnAddress('cc', $address, $name);&nbsp; }&nbsp; /*** Adds a "Bcc" address.* Note: this function works with the SMTP mailer on win32, not with the "mail" mailer.* @param string $address* @param string $name* @return boolean true on success, false if address already used*/&nbsp; public function AddBCC($address, $name = '') {&nbsp;&nbsp;&nbsp; return $this-&gt;AddAnAddress('bcc', $address, $name);&nbsp; }&nbsp; /*** Adds a "Reply-to" address.* @param string $address* @param string $name* @return boolean*/&nbsp; public function AddReplyTo($address, $name = '') {&nbsp;&nbsp;&nbsp; return $this-&gt;AddAnAddress('ReplyTo', $address, $name);&nbsp; }&nbsp; /*** Adds an address to one of the recipient arrays* Addresses that have been added already return false, but do not throw exceptions* @param string $kind One of 'to', 'cc', 'bcc', 'ReplyTo'* @param string $address The email address to send to* @param string $name* @return boolean true on success, false if address already used or invalid in some way* @access protected*/&nbsp; protected function AddAnAddress($kind, $address, $name = '') {&nbsp;&nbsp;&nbsp; if (!preg_match('/^(to|cc|bcc|ReplyTo)$/', $kind)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($this-&gt;Lang('Invalid recipient array').': '.$kind);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException('Invalid recipient array: ' . $kind);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo $this-&gt;Lang('Invalid recipient array').': '.$kind;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return false;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $address = trim($address);&nbsp;&nbsp;&nbsp; $name = trim(preg_replace('/[\r\n]+/', '', $name)); //Strip breaks and trim&nbsp;&nbsp;&nbsp; if (!self::ValidateAddress($address)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($this-&gt;Lang('invalid_address').': '. $address);&nbsp;&nbsp;&nbsp;&nbsp; /* if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('invalid_address').': '.$bcc);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo $this-&gt;Lang('invalid_address').': '.$address;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return false;*/&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if ($kind != 'ReplyTo') {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!isset($this-&gt;all_recipients[strtolower($address)])) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; array_push($this-&gt;$kind, array($address, $name));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;all_recipients[strtolower($address)] = true;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!array_key_exists(strtolower($address), $this-&gt;ReplyTo)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;ReplyTo[strtolower($address)] = array($address, $name);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; return false;}/*** Set the From and FromName properties* @param string $address* @param string $name* @return boolean*/&nbsp; public function SetFrom($address, $name = '', $auto = 1) {&nbsp;&nbsp;&nbsp; $address = trim($address);&nbsp;&nbsp;&nbsp; $name = trim(preg_replace('/[\r\n]+/', '', $name)); //Strip breaks and trim&nbsp;&nbsp;&nbsp; if (!self::ValidateAddress($address)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($this-&gt;Lang('invalid_address').': '. $address);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('invalid_address').': '.$address);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 