////////////////////////////////&nbsp; // METHODS, MAIL SENDING&nbsp; /////////////////////////////////////////////////&nbsp; /*** Creates message and assigns Mailer. If the message is* not sent successfully then it returns false. Use the ErrorInfo* variable to view description of the error.* @return bool*/&nbsp; public function Send() {&nbsp;&nbsp;&nbsp; try {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!$this-&gt;PreSend()) return false;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;PostSend();&nbsp;&nbsp;&nbsp; } catch (phpmailerException $e) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($e-&gt;getMessage());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw $e;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return false;&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; protected function PreSend() {&nbsp;&nbsp;&nbsp; try {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ((count($this-&gt;to) + count($this-&gt;cc) + count($this-&gt;bcc)) &lt; 1) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('provide_address'), self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Set whether the message is multipart/alternative&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!empty($this-&gt;AltBody)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;ContentType = 'multipart/alternative';&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;error_count = 0; // reset errors&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetMessageType();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //Refuse to send an empty message&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (empty($this-&gt;Body)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('empty_message'), self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;MIMEHeader = $this-&gt;CreateHeader();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;MIMEBody = $this-&gt;CreateBody();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // digitally sign with DKIM if enabled&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;DKIM_domain &amp;&amp; $this-&gt;DKIM_private) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $header_dkim = $this-&gt;DKIM_Add($this-&gt;MIMEHeader, $this-&gt;EncodeHeader($this-&gt;SecureHeader($this-&gt;Subject)), $this-&gt;MIMEBody);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;MIMEHeader = str_replace("\r\n", "\n", $header_dkim) . $this-&gt;MIMEHeader;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;&nbsp;&nbsp;&nbsp; } catch (phpmailerException $e) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($e-&gt;getMessage());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw $e;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return false;&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; protected function PostSend() {&nbsp;&nbsp;&nbsp; try {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Choose the mailer and send through it&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; switch($this-&gt;Mailer) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 'sendmail':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;SendmailSend($this-&gt;MIMEHeader, $this-&gt;MIMEBody);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; case 'smtp':&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;SmtpSend($this-&gt;MIMEHeader, $this-&gt;MIMEBody);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; default:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;MailSend($this-&gt;MIMEHeader, $this-&gt;MIMEBody);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; } catch (phpmailerException $e) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;SetError($e-&gt;getMessage());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;exceptions) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw $e;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; echo $e-&gt;getMessage()."\n";&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return false;&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; /*** Sends mail using the $Sendmail program.* @param string $header The message headers* @param string $body The message body* @access protected* @return bool*/&nbsp; protected function SendmailSend($header, $body) {&nbsp;&nbsp;&nbsp; if ($this-&gt;Sender != '') {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $sendmail = sprintf("%s -oi -f %s -t", escapeshellcmd($this-&gt;Sendmail), escapeshellarg($this-&gt;Sender));&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $sendmail = sprintf("%s -oi -t", escapeshellcmd($this-&gt;Sendmail));&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if ($this-&gt;SingleTo === true) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach ($this-&gt;SingleToArray as $key =&gt; $val) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!@$mail = popen($sendmail, 'w')) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('execute') . $this-&gt;Sendmail, self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fputs($mail, "To: " . $val . "\n");&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fputs($mail, $header);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fputs($mail, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $result = pclose($mail);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($result == 0) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $val, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($result != 0) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('execute') . $this-&gt;Sendmail, self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(!@$mail = popen($sendmail, 'w')) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('execute') . $this-&gt;Sendmail, self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fputs($mail, $header);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fputs($mail, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $result = pclose($mail);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($result == 0) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $this-&gt;to, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($result != 0) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('execute') . $this-&gt;Sendmail, self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return true;&nbsp; }&nbsp; /*** Sends mail using the PHP mail() function.* @param string $header The message headers* @param string $body The message body* @access protected* @return bool*/&nbsp; protected function MailSend($header, $body) {&nbsp;&nbsp;&nbsp; $toArr = array();&nbsp;&nbsp;&nbsp; foreach($this-&gt;to as $t) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $toArr[] = $this-&gt;AddrFormat($t);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $to = implode(', ', $toArr);&nbsp;&nbsp;&nbsp; if (empty($this-&gt;Sender)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $params = "-oi -f %s";&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $params = sprintf("-oi -f %s", $this-&gt;Sender);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if ($this-&gt;Sender != '' and !ini_get('safe_mode')) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $old_from = ini_get('sendmail_from');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ini_set('sendmail_from', $this-&gt;Sender);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;SingleTo === true &amp;&amp; count($toArr) &gt; 1) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach ($toArr as $key =&gt; $val) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $rt = @mail($val, $this-&gt;EncodeHeader($this-&gt;SecureHeader($this-&gt;Subject)), $body, $header, $params);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($rt == 1) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $val, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $rt = @mail($to, $this-&gt;EncodeHeader($this-&gt;SecureHeader($this-&gt;Subject)), $body, $header, $params);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($rt == 1) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $to, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;SingleTo === true &amp;&amp; count($toArr) &gt; 1) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach ($toArr as $key =&gt; $val) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $rt = @mail($val, $this-&gt;EncodeHeader($this-&gt;SecureHeader($this-&gt;Subject)), $body, $header, $params);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($rt == 1) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $val, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $rt = @mail($to, $this-&gt;EncodeHeader($this-&gt;SecureHeader($this-&gt;Subject)), $body, $header);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = ($rt == 1) ? 1 : 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $to, $this-&gt;cc, $this-&gt;bcc, $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if (isset($old_from)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ini_set('sendmail_from', $old_from);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(!$rt) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('instantiate'), self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return true;&nbsp; }&nbsp; /*** Sends mail via SMTP using PhpSMTP* Returns false if there is a bad MAIL FROM, RCPT, or DATA input.* @param string $header The message headers* @param string $body The message body* @uses SMTP* @access protected* @return bool*/&nbsp; protected function SmtpSend($header, $body) {&nbsp;&nbsp;&nbsp; require_once $this-&gt;PluginDir . 'anthill.smtp.php';&nbsp;&nbsp;&nbsp; //require_once $this-&gt;PluginDir . 'class.smtp.php';&nbsp;&nbsp;&nbsp; $bad_rcpt = array();&nbsp;&nbsp;&nbsp; if(!$this-&gt;SmtpConnect()) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('smtp_connect_failed'), self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $smtp_from = ($this-&gt;Sender == '') ? $this-&gt;From : $this-&gt;Sender;&nbsp;&nbsp;&nbsp; if(!$this-&gt;smtp-&gt;Mail($smtp_from)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('from_failed') . $smtp_from, self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; // Attempt to send attach all recipients&nbsp;&nbsp;&nbsp; foreach($this-&gt;to as $to) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$this-&gt;smtp-&gt;Recipient($to[0])) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $bad_rcpt[] = $to[0];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $to[0], '', '', $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, $to[0], '', '', $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; foreach($this-&gt;cc as $cc) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$this-&gt;smtp-&gt;Recipient($cc[0])) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $bad_rcpt[] = $cc[0];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, '', $cc[0], '', $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, '', $cc[0], '', $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; foreach($this-&gt;bcc as $bcc) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$this-&gt;smtp-&gt;Recipient($bcc[0])) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $bad_rcpt[] = $bcc[0];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 0;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, '', '', $bcc[0], $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // implement call back function if it exists&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $isSent = 1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;doCallback($isSent, '', '', $bcc[0], $this-&gt;Subject, $body);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if (count($bad_rcpt) &gt; 0 ) { //Create error message for any bad addresses&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $badaddresses = implode(', ', $bad_rcpt);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('recipients_failed') . $badaddresses);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if(!$this-&gt;smtp-&gt;Data($header . $body)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('data_not_accepted'), self::STOP_CRITICAL);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; if($this-&gt;SMTPKeepAlive == true) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Reset();&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return true;&nbsp; }&nbsp; /*** Initiates a connection to an SMTP server.* Returns false if the operation failed.* @uses SMTP* @access public* @return bool*/&nbsp; public function SmtpConnect() {&nbsp;&nbsp;&nbsp; if(is_null($this-&gt;smtp)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp = new SMTP();&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;do_debug = $this-&gt;SMTPDebug;&nbsp;&nbsp;&nbsp; $hosts = explode(';', $this-&gt;Host);&nbsp;&nbsp;&nbsp; $index = 0;&nbsp;&nbsp;&nbsp; $connection = $this-&gt;smtp-&gt;Connected();&nbsp;&nbsp;&nbsp; // Retry while there is no connection&nbsp;&nbsp;&nbsp; try {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while($index &lt; count($hosts) &amp;&amp; !$connection) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $hostinfo = array();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (preg_match('/^(.+):([0-9]+)$/', $hosts[$index], $hostinfo)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $host = $hostinfo[1];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $port = $hostinfo[2];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $host = $hosts[$index];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $port = $this-&gt;Port;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $tls = ($this-&gt;SMTPSecure == 'tls');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $ssl = ($this-&gt;SMTPSecure == 'ssl');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;smtp-&gt;Connect(($ssl ? 'ssl://':'').$host, $port, $this-&gt;Timeout)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $hello = ($this-&gt;Helo != '' ? $this-&gt;Helo : $this-&gt;ServerHostname());&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Hello($hello);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($tls) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$this-&gt;smtp-&gt;StartTLS()) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('tls'));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //We must resend HELO after tls negotiation&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Hello($hello);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $connection = true;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;SMTPAuth) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$this-&gt;smtp-&gt;Authenticate($this-&gt;Username, $this-&gt;Password)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('authenticate'));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $index++;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (!$connection) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw new phpmailerException($this-&gt;Lang('connect_host'));&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; } catch (phpmailerException $e) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Reset();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; throw $e;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; return true;&nbsp; }&nbsp; /*** Closes the active SMTP session if one exists.* @return void*/&nbsp; public function SmtpClose() {&nbsp;&nbsp;&nbsp; if(!is_null($this-&gt;smtp)) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;smtp-&gt;Connected()) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Quit();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;smtp-&gt;Close();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; }&nbsp; }&nbsp; /*** Sets the language for all class error messages.* Returns false if it cannot load the language file. The default language is English.* @param string $langcode ISO 639-1 2-character language code (e.g. Portuguese: "br")* @param string $lang_path Path to the language file directory* @access public*/&nbsp; function SetLanguage($langcode = 'en', $lang_path = 'language/') {&nbsp;&nbsp;&nbsp; //Define full set of translatable strings&nbsp;&nbsp;&nbsp; $PHPMAILER_LANG = array(&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'provide_address' =&gt; 'You must provide at least one recipient email address.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'mailer_not_supported' =&gt; ' mailer is not supported.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'execute' =&gt; 'Could not execute: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'instantiate' =&gt; 'Could not instantiate mail function.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'authenticate' =&gt; 'SMTP Error: Could not authenticate.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'from_failed' =&gt; 'The following From address failed: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'recipients_failed' =&gt; 'SMTP Error: The following recipients failed: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'data_not_accepted' =&gt; 'SMTP Error: Data not accepted.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'connect_host' =&gt; 'SMTP Error: Could not connect to SMTP host.',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'file_access' =&gt; 'Could not access file: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'file_open' =&gt; 'File Error: Could not open file: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'encoding' =&gt; 'Unknown encoding: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'signing' =&gt; 'Signing Error: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'smtp_error' =&gt; 'SMTP server error: ',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'empty_message' =&gt; 'Message body empty',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'invalid_address' =&gt; 'Invalid address',&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 'variable_set' =&gt; 'Cannot set or reset variable: '&nbsp;&nbsp;&nbsp; );&nbsp;&nbsp;&nbsp; //Overwrite language-specific strings. This way we'll never have missing translations - no more "language string failed to load"!&nbsp;&nbsp;&nbsp; $l = true;&nbsp;&nbsp;&nbsp; if ($langcode != 'en') { //There is no English translation file&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $l = @include $lang_path.'phpmailer.lang-'.$langcode.'.php';&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $this-&gt;language = $PHPMAILER_LANG;&nbsp;&nbsp;&nbsp; return ($l == true); //Returns false if language not found&nbsp; }&nbsp; /*** Return the current array of language strings* @return array*/&nbsp; public function GetTranslations() {&nbsp;&nbsp;&nbsp; return $this-&gt;language;&nbsp; }&nbsp; /////////////////////////////////////////////////&nbsp; // METHODS, MESSAGE CREATION&nbsp; /////////////////////////////////////////////////&nbsp; /*** Creates recipient headers.* @access public* @return string*/&nbsp; public function AddrAppend($type, $addr) {&nbsp;&nbsp;&nbsp; $addr_str = $type . ': ';&nbsp;&nbsp;&nbsp; $addresses = array();&nbsp;&nbsp;&nbsp; foreach ($addr as $a) {&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $addresses[] = $this-&gt;AddrFormat($a);&nbsp;&nbsp;&nbsp; }&nbsp;&nbsp;&nbsp; $addr_str .= implode(', ', $addresses);&nbsp;&nbsp;&nbsp; $addr_str .= $this-&gt;LE;&nbsp;&nbsp;&nbsp; return $addr_str;&nbsp; }&nbsp; /*** Formats an address correctly.* @access public* @return string*/&nbsp; public function AddrFormat($addr) {&nbsp;&nbsp;&nbsp; if (empty(