<br />
&lt;p class=&quot;code_block&quot; style=&quot;margin: -12px 0px;&quot;&gt;if (!move_uploaded_file($_FILES['files']['tmp_name'][$source], drupal_realpath($file-&gt;uri))) {&lt;/p&gt;<br />
