&lt;pre class=&quot;code_block&quot; style=&quot;margin: -10px 0px -30px 0px; line-height: 1.1em&quot;&gt;{if (((isset($product.show_price) &amp;&amp; $product.show_price) || (isset($product.available_for_order) &amp;&amp; $product.available_for_order)))}&lt;/pre&gt;  <br />
